sys_script_include.script:
var fetchData = Class.create();
fetchData.prototype = {
    initialize: function() {
    },

	getRec:function(tableName,limit){

		var answer = [];
		var gr = new GlideRecord(tableName);
		gr.addActiveQuery();
		if(limit && limit >0){
			gr.setLimit(limit);
		}
		gr.query();

		while(gr.next()){
			var obj ={};
			obj.number =gr.getDisplayValue();
			obj.sys_id =gr.getUniqueValue();
			answer.push(obj);
		}
		return answer;
	},
    type: 'fetchData'
};
